import React, { useState } from 'react';
import CsvUploader from './components/CsvUploader';
import Header_Interpret from './components/header_interpret';
import GenerateQuestions from './components/GenerateQuestions'; // Don't forget to import this
import './App.css';

function App() {
  const [csvHeaders, setCsvHeaders] = useState([]);
  const [showInterpretations, setShowInterpretations] = useState(false);
  const [datasetInterpretation, setDatasetInterpretation] = useState('');
  const [columnInterpretations, setColumnInterpretations] = useState({});

  const handleInterpretHeaders = () => {
    setShowInterpretations(true);
  };

  const handleInterpretationResults = (datasetInterpretation, columnInterpretations) => {
    setDatasetInterpretation(datasetInterpretation);
    setColumnInterpretations(columnInterpretations);
  };

  return (
    <div className="app-container">
      <header className="app-header">
        <h1>CSV and OpenAI Integration</h1>
      </header>
      <div className="app-content">
        <CsvUploader setCsvHeaders={setCsvHeaders} csvHeaders={csvHeaders} />
        <button onClick={handleInterpretHeaders}>Interpret Headers</button>
        {showInterpretations && 
          <Header_Interpret 
            csvHeaders={csvHeaders}
            onInterpretationComplete={handleInterpretationResults} 
          />
        }
        {datasetInterpretation && columnInterpretations &&
          <GenerateQuestions 
            datasetDescription={datasetInterpretation} 
            columnInfo={columnInterpretations} 
          />
        }
      </div>
    </div>
  );
}

export default App;
