import React, { useState } from "react";
import Papa from "papaparse";
import { useDropzone } from "react-dropzone";

function CsvUploader({ setCsvHeaders, csvHeaders }) {

  const [csvData, setCsvData] = useState([]);
  const [fileName, setFileName] = useState("");

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    setFileName(file.name);

    Papa.parse(file, {
      complete: (result) => {
        const headers = result.meta.fields;
        const data = result.data.slice(0, 10);
        setCsvHeaders(headers);
        setCsvData(data);
      },
      header: true,
    });
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: ".csv",
  });

  return (
    <a href="#" className="box-container" {...getRootProps()}>
      Upload CSV File
      <div className="dropzone">
        <input {...getInputProps()} />
      </div>
      {csvHeaders.length > 0 && (
        <div>
          <h2>CSV File: {fileName}</h2>

          <h2>Head of Dataframe:</h2>
          <div style={{ overflowX: 'auto' }}> {/* This div enables horizontal scrolling */}
            <table>
              <thead>
                <tr>
                  {csvHeaders.map((header) => (
                    <th key={header}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {csvData.map((row, index) => (
                  <tr key={index}>
                    {csvHeaders.map((header) => (
                      <td key={header}>{row[header]}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </a>
  );
}

export default CsvUploader;
