const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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';

const Header_Interpret = ({ csvHeaders }) => {
  const [interpretations, setInterpretations] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const interpretHeader = useCallback(async (header) => {
    const systemMessage = {
      role: 'system',
      content: `Interpret the CSV column header "${header}" in a detailed manner.`,
    };

    const messages = [systemMessage];

    try {
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: messages,
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );

      return response.data.choices[0].message.content.trim();
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      return 'Error interpreting header';
    }
  }, []);

  useEffect(() => {
    const fetchInterpretations = async () => {
      setLoading(true);
      setError(null);
      const newInterpretations = {};

      for (const header of csvHeaders) {
        newInterpretations[header] = await interpretHeader(header);
      }

      setInterpretations(newInterpretations);
      setLoading(false);
    };

    if (csvHeaders.length > 0) {
      fetchInterpretations();
    }
  }, [csvHeaders, interpretHeader]);

    return (
        <>
            <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center', height: '100px' }}>
                {loading && <p>Loading interpretations...</p>}
            </div>
            {Object.keys(interpretations).length > 0 && (
                <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
                    <h2>Interpretations</h2>
                    {Object.entries(interpretations).map(([header, interpretation]) => (
                        <div key={header}>
                            <strong>{header}:</strong> {interpretation}
                        </div>
                    ))}
                </div>
            )}
            {error && <p>{error}</p>}
        </>
    );
};

export default Header_Interpret;
