import { useState, useEffect, useCallback, useMemo } from "react";
const { Configuration, OpenAIApi } = require("openai");

const Header_Interpret = () => {
  // Memoize the openai object to avoid re-initialization on every render
  const openai = useMemo(() => {
    const configuration = new Configuration({
      apiKey: "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",
    });
    return new OpenAIApi(configuration);
  }, []);

  const [csvHeaders, setCsvHeaders] = useState([]); // We'll keep this assuming you will use it elsewhere
  const [interpretations, setInterpretations] = useState({});
  const [loading, setLoading] = useState(false);

  const interpretHeader = useCallback(async (header) => {
    const result = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: `Interpret the column header "${header}" in a CSV file.`,
      temperature: 0.5,
      max_tokens: 100,
    });
    return result.data.choices[0].text.trim();
  }, [openai]);
 // Added openai to the dependency array


  useEffect(() => {
    const fetchInterpretations = async () => {
      setLoading(true);
      const newInterpretations = {};

      for (const header of csvHeaders) {
        newInterpretations[header] = await interpretHeader(header);
      }

      setInterpretations(newInterpretations);
      setLoading(false);
    };

    if (csvHeaders.length > 0) {
      fetchInterpretations();
    }
  }, [csvHeaders, interpretHeader]);

  return (
    <>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          height: "100vh",
        }}
      >
        {/* Your existing form can stay here */}
        {loading && <p>Loading interpretations...</p>}
      </div>
      {Object.keys(interpretations).length > 0 && (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <h2>Interpretations</h2>
          {Object.entries(interpretations).map(([header, interpretation]) => (
            <div key={header}>
              <strong>{header}:</strong> {interpretation}
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default Header_Interpret;
