import React, { useState, useCallback } from 'react';
import axios from 'axios';

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = 'your-api-key-here';  // Replace with your actual API key

const Header_Interpret = ({ csvHeaders }) => {
  const [interpretations, setInterpretations] = useState({});
  const [datasetInterpretation, setDatasetInterpretation] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const interpretHeader = useCallback(async (header) => {
    try {
      const systemMessage = {
        role: 'system',
        content: `Interpret the CSV column header "${header}" in a very brief but detailed manner. Not more than two sentences`,
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      return response.data.choices[0].message.content.trim();
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      return 'Error interpreting header';
    }
  }, []);

  const interpretDataset = useCallback(async () => {
    try {
      const systemMessage = {
        role: 'system',
        content: `Provide an overall interpretation of a dataset in 2 sentences: ${csvHeaders.join(', ')}. Do not create an interpretation per column, just the dataset`,
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      return response.data.choices[0].message.content.trim();
    } catch (error) {
      console.error('Error response from OpenAI:', error.response?.data);
      return 'Error interpreting dataset';
    }
  }, [csvHeaders]);

  const interpretHeaders = async () => {
    setLoading(true);
    setError(null);
    const newInterpretations = {};

    for (const header of csvHeaders) {
      newInterpretations[header] = await interpretHeader(header);
    }

    setInterpretations(newInterpretations);
    setDatasetInterpretation(await interpretDataset());
    setLoading(false);
  };

  return (
    <>
      <button onClick={interpretHeaders}>Interpret Headers</button>
      <div>
        {loading && <p>Loading interpretations...</p>}
      </div>
      <div>
        <h2>Overall Dataset Interpretation</h2>
        <textarea
          value={datasetInterpretation}
          onChange={(e) => setDatasetInterpretation(e.target.value)}
        />
      </div>
      {Object.keys(interpretations).length > 0 && (
        <div>
          <h2>Interpretations</h2>
          {Object.entries(interpretations).map(([header, interpretation]) => (
            <div key={header}>
              <strong>{header}:</strong>
              <input 
                type="text" 
                value={interpretation}
                onChange={(e) => {
                  setInterpretations({
                    ...interpretations,
                    [header]: e.target.value,
                  });
                }}
              />
            </div>
          ))}
        </div>
      )}
      {error && <p>{error}</p>}
    </>
  );
};

export default Header_Interpret;
