import React, { useState } from "react";
import Papa from "papaparse";
import { useDropzone } from "react-dropzone";

function CsvUploader() {
  const [csvHeaders, setCsvHeaders] = useState([]);
  const [csvData, setCsvData] = useState([]);

  const onDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];

    Papa.parse(file, {
      complete: (result) => {
        const headers = result.meta.fields; // Extract CSV headers
        const data = result.data.slice(0, 10); // Read the first 10 rows of data
        setCsvHeaders(headers);
        setCsvData(data);
      },
      header: true, // Treat the first row as headers.
    });
  };

  const { getRootProps, getInputProps } = useDropzone({
    onDrop,
    accept: ".csv",
  });

  return (
    <a href="#" className="box-container" {...getRootProps()}>
      {/* Text is inside the <a> tag */}
      Upload CSV File
      <div className="dropzone">
        <input {...getInputProps()} />
      </div>
      {csvHeaders.length > 0 && (
        <div>
          <h2>CSV Headers:</h2>
          <table>
            <thead>
              <tr>
                {csvHeaders.map((header) => (
                  <th key={header}>{header}</th>
                ))}
              </tr>
            </thead>
          </table>
          <h2>Head of Dataframe:</h2>
          <table>
            <thead>
              <tr>
                {csvHeaders.map((header) => (
                  <th key={header}>{header}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              {csvData.map((row, index) => (
                <tr key={index}>
                  {csvHeaders.map((header) => (
                    <td key={header}>{row[header]}</td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </a>
  );
}

export default CsvUploader;
