import React, { useState, useCallback } from 'react';
import axios from 'axios';

const API_URL = 'https://api.openai.com/v1/chat/completions';
const API_KEY = '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'; // Replace with your actual API key

const GenerateQuestions = ({ datasetDescription, columnInfo }) => {
  const [generatedQuestions, setGeneratedQuestions] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const generatePrompt = () => {
    return `Dataset description: ${datasetDescription}. Columns: ${Object.entries(columnInfo).map(([k, v]) => `${k} - ${v}`).join(', ')}. `
      + "Hey ChatGPT, your goal here is to create questions about this dataset that can be answered through data visualization practices in Python..."; // Complete this part based on your initial prompt
  };

  const generateQuestions = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const prompt = generatePrompt();
      const systemMessage = {
        role: 'system',
        content: prompt,
      };
      const response = await axios.post(
        API_URL,
        {
          model: 'gpt-3.5-turbo',
          messages: [systemMessage],
        },
        {
          headers: {
            Authorization: `Bearer ${API_KEY}`,
            'Content-Type': 'application/json',
          },
        }
      );
      setGeneratedQuestions(response.data.choices[0].message.content.trim());
    } catch (e) {
      setError('An error occurred while generating questions.');
    }
    setLoading(false);
  }, [datasetDescription, columnInfo]);

  return (
    <div>
      <button onClick={generateQuestions}>Generate</button>
      {loading && <p>Loading questions...</p>}
      {generatedQuestions && <textarea value={generatedQuestions} readOnly />}
      {error && <p>{error}</p>}
    </div>
  );
};

export default GenerateQuestions;
